   function resize(){
        let vw = document.documentElement.clientWidth;
        let vh = document.documentElement.clientHeight;
        let h = vh 
        let w = (h/100*178);
        if(w >= vw) {
            w = vw 
            h =vw/100*56
        }
        game.style.width = w +'px';
        game.style.height = h +'px';
        fontResize(modalText, 1.8);
        fontResize(text, 4);
        }
   window.onresize = resize;
   
   function fontResize(item, index) {
    item.style.fontSize='';
    let length = item.textContent.length,
    w = parseInt(game.style.width),
    h = parseInt(game.style.height);
    fontSize = ((Math.sqrt(w*h))/100)*index;
    item.style.fontSize = `${fontSize}px`;
    item.style.lineHeight = `${fontSize*1.1}px`;
    }
   
let trueQuestions = [...ctrueQuestions],
    falseQuestions = [...cfalseQuestions];

function getRandomQuestion(arr) {
    return arr.splice((Math.floor(Math.random() * arr.length)), 1);
};
function end () {
    modal.classList.remove('game__modal_hiden');
    gameArea.classList.add('game__area_hiden');
    wraper.classList.toggle('game__wraper_active');
    modalText.innerHTML = "";
    modalText.innerHTML = `К сожалению, общение с В. И. Далем закончилось. Вы допустили 5 ошибок. Не расстраивайтесь! В следующий раз обязательно получится.`
        x.onclick =() => {
           location.reload() 
        }
};
function showQuestion() {
    let random = Math.floor(Math.random() * 2);
        text.innerHTML = '';
        if (random) {
          text.innerHTML = getRandomQuestion(trueQuestions);
          
          answer = true
        } else {
            text.innerHTML = getRandomQuestion(falseQuestions);
            answer = false
        }
    };
function waitAnswer() {
    yes.onclick = ()=> {
            if(answer) {
                point++
                game.dispatchEvent(endRound);
            } else {
                antipoint++
                game.dispatchEvent(error);
                if(antipoint==5) {
                    game.dispatchEvent(endGame);
                }else {
                    game.dispatchEvent(endRound);
                }
            }
    }
    no.onclick = ()=> {
        if(!answer) {
            point++
            game.dispatchEvent(endRound);
        } else {
            antipoint++
            game.dispatchEvent(error);
            if(antipoint==5) {
                game.dispatchEvent(endGame);
            }else {
                game.dispatchEvent(endRound);
            }
            
        }
    }
}

function onerror(){
    if(antipoint<=5)
    errors[antipoint-1].classList.add('game__error_active');
}
function start() {
    showQuestion();
    waitAnswer();
}

start()
game.addEventListener('error', onerror);
game.addEventListener('endRound', start);
game.addEventListener('endGame', end);



 

